      go to 700
 603  msg = 'DLSODES- ISTATE.gt.1 but DLSODES not initialized. '
      call xerrwd (msg, 50, 3, 0, 0, 0, 0, 0, 0.0D0, 0.0D0)
      go to 700
 604  msg = 'DLSODES- NEQ (=I1) .lt. 1     '
      call xerrwd (msg, 30, 4, 0, 1, neq(1), 0, 0, 0.0D0, 0.0D0)
