 605  msg = 'DLSODES- ISTATE = 3 and NEQ increased (I1 to I2). '
      call xerrwd (msg, 50, 5, 0, 2, n, neq(1), 0, 0.0D0, 0.0D0)
      go to 700
 606  msg = 'DLSODES- ITOL (=I1) illegal.  '
