 622  msg='DLSODES- TOUT(=R1) too close to T(=R2) to start integration.'
      call xerrwd (msg, 60, 22, 0, 0, 0, 0, 2, tout, t)
      go to 700
 623  msg='DLSODES- ITASK = I1 and TOUT (=R1) behind TCUR - HU (= R2)  '
      call xerrwd (msg, 60, 23, 0, 1, itask, 0, 2, tout, tp)
